mapper.select:
select 
              menu_id AS menuId,
              parent_id AS parentId,
              name AS name,
              url AS url,
              icon AS icon,
              create_user_id AS createUserId,
              create_time AS createTime,
              update_time AS updateTime,
              delete_flag AS deleteFlag
  from sys_menu


                  and menu_id = #{menuId}


                  and parent_id = #{parentId}


                  and name = #{name}


                  and url = #{url}


                  and icon = #{icon}


                  and create_user_id = #{createUserId}


                  and create_time = #{createTime}


                  and update_time = #{updateTime}


                  and delete_flag = #{deleteFlag}


            Limit 1

select 
              menu_id AS menuId,
              parent_id AS parentId,
              name AS name,
              url AS url,
              icon AS icon,
              create_user_id AS createUserId,
              create_time AS createTime,
              update_time AS updateTime,
              delete_flag AS deleteFlag
  from sys_menu


                  and menu_id = #{menuId}


                  and parent_id = #{parentId}


                  and name = #{name}


                  and url = #{url}


                  and icon = #{icon}


                  and create_user_id = #{createUserId}


                  and create_time = #{createTime}


                  and update_time = #{updateTime}


                  and delete_flag = #{deleteFlag}
