mapper.select:
select 
              role_id AS roleId,
              role_name AS roleName,
              remark AS remark,
              create_user_id AS createUserId,
              create_time AS createTime,
              update_time AS updateTime,
              delete_flag AS deleteFlag
  from sys_role


                  and role_id = #{role_id}


                  and role_name = #{roleName}


                  and remark = #{remark}


                  and create_user_id = #{createUserId}


                  and create_time = #{createTime}


                  and update_time = #{updateTime}


                  and delete_flag = #{deleteFlag}


            Limit 1

select 
              role_id,
              role_name AS roleName,
              remark AS remark,
              create_user_id AS createUserId,
              create_time AS createTime,
              update_time AS updateTime,
              delete_flag AS deleteFlag
  from sys_role


                  and role_id = #{role_id}


                  and role_name = #{roleName}


                  and remark = #{remark}


                  and create_user_id = #{createUserId}


                  and create_time = #{createTime}


                  and update_time = #{updateTime}


                  and delete_flag = #{deleteFlag}

select m.menu_id, m.parent_id AS parentId, m.name AS name, m.url AS url, m.icon AS icon, m.create_user_id AS createUserId from sys_role r left join sys_role_menu rm on r.role_id = rm.role_id left join sys_menu m on rm.menu_id = m.menu_id
rm.delete_flag = 0 and m.delete_flag = 0
and r.role_id = #{role_id}

mapper.update:
update sys_role set 
       role_name=#{roleName},
       remark=#{remark},
       create_user_id=#{createUserId},
       delete_flag=#{deleteFlag}

           where role_id=#{role_id} and update_time = #{updateTime}
